mapper.select:
/* NOT_SQL_LOG */
SELECT COUNT(*)
FROM User
WHERE EMAIL = #{email}

/* NOT_SQL_LOG */
SELECT COUNT(*)
FROM User
WHERE PHONE = #{phone}

/* NOT_SQL_LOG */
SELECT REFRESH_TOKEN
  FROM Refresh_Token
 WHERE IDX = #{idx}

mapper.insert:
/* NOT_SQL_LOG */
INSERT INTO
       Refresh_Token (
                     USER_EMAIL
                   , ACCESS_TOKEN
                   , REFRESH_TOKEN
                   , REFRESH_TOKEN_EXPIRATION_AT
)
VALUES (
       #{userEmail}
     , #{accessToken}
     , #{refreshToken}
     , #{refreshTokenExpirationAt}
)
ON DUPLICATE KEY UPDATE
             IDX = ( SELECT idx FROM
                ( SELECT (idx + 1) AS idx FROM Refresh_Token B ORDER BY idx DESC LIMIT 1)
             AS A)
      , ACCESS_TOKEN = #{accessToken}
      , REFRESH_TOKEN = #{refreshToken}
      , REFRESH_TOKEN_EXPIRATION_AT = #{refreshTokenExpirationAt}
      , USER_EMAIL = #{userEmail}

/* NOT_SQL_LOG */
INSERT INTO User
          ( EMAIL
          , PASSWORD
          , ROLE
          , ROLE_CODE_IDX
          , STATUS
          , NAME
          , PHONE
          , GENDER
          , IS_AGREEMENT
          , WRITE_AT
          )
   VALUES ( #{email}
          , #{password}
          , #{role}
          , #{roleCodeIdx}
          , #{status}
          , #{name}
          , #{phone}
          , #{gender}
          , #{isAgreement}
          , NOW()
          )
