mapper.select:
SELECT REFRESH_TOKEN
  FROM Refresh_Token
 WHERE IDX = #{idx}

mapper.insert:
INSERT INTO
       Refresh_Token (
                     USER_EMAIL
                   , ACCESS_TOKEN
                   , REFRESH_TOKEN
                   , REFRESH_TOKEN_EXPIRATION_AT
)
VALUES (
       #{userEmail}
     , #{accessToken}
     , #{refreshToken}
     , #{refreshTokenExpirationAt}
)
ON DUPLICATE KEY UPDATE
             IDX = ( SELECT idx FROM
                ( SELECT (idx + 1) AS idx FROM Refresh_Token B ORDER BY idx DESC LIMIT 1)
             AS A)
      , ACCESS_TOKEN = #{accessToken}
      , REFRESH_TOKEN = #{refreshToken}
      , REFRESH_TOKEN_EXPIRATION_AT = #{refreshTokenExpirationAt}
      , USER_EMAIL = #{userEmail}

mapper.select:
SELECT COUNT(*)
  FROM User
 WHERE EMAIL = #{email}

SELECT COUNT(*)
  FROM User
 WHERE PHONE = #{phone}

mapper.insert:
INSERT INTO
       User (
             EMAIL
           , PASSWORD
           , ROLE
           , ROLE_DETAIL
           , STATUS
           , NAME
           , PHONE
           , GENDER
           , IS_AGREEMENT
           , WRITE_AT
)
VALUES (
            #{email}
          , #{password}
          , #{role}
          , #{roleDetail}
          , #{status}
          , #{name}
          , #{phone}
          , #{gender}
          , #{isAgreement}
          , NOW()
)
