mapper.select:
/* NOT_SQL_LOG */
SELECT COUNT(*)
  FROM Board_config
 WHERE IDX = #{idx)}

/* NOT_SQL_LOG */
SELECT COUNT(*)
  FROM Board_config
 WHERE TYPE = #{type}

/* NOT_SQL_LOG */ SELECT IDX , TYPE , NAME , ROLE , LIST_COUNT , PAGE_COUNT FROM Board_config
WHERE NAME LIKE CONCAT('%',#{pageDTO.keyword},'%')
ORDER BY IDX DESC LIMIT #{pageDTO.pageIndex}, #{pageDTO.pageSize}

mapper.insert:
/* NOT_SQL_LOG */
INSERT INTO
    Board_config (
                 TYPE
               , NAME
               , ROLE
               , LIST_COUNT
               , PAGE_COUNT
)
VALUES (
       #{type}
     , #{name}
     , #{role}
     , #{listCount}
     , #{pageCount}
       )

mapper.update:
/* NOT_SQL_LOG */
UPDATE Board_config
   SET NAME = #{name}
     , ROLE = #{role}
     , LIST_COUNT = #{listCount}
     , PAGE_COUNT = #{pageCount}
WHERE IDX = #{idx};
