mapper.select:
SELECT B.IDX , B.TITLE , B.VIEW_COUNT , B.WRITE_AT , U.NAME , U.EMAIL FROM Board B LEFT JOIN User U ON U.IDX = B.USER_IDX
WHERE U.NAME LIKE CONCAT('%',#{pageDTO.keyword},'%') OR U.EMAIL LIKE CONCAT('%',#{pageDTO.keyword},'%') OR B.TITLE LIKE CONCAT('%',#{pageDTO.keyword},'%')
ORDER BY B.IDX DESC LIMIT #{pageDTO.pageIndex}, #{pageDTO.pageSize}

mapper.insert:
INSERT INTO
 Board (
        BOARD_CONFIG_IDX
      , BOARD_CATEGORY_CONFIG_IDX
      , USER_IDX
      , STATUS
      , TITLE
      , CONTENTS
      , VIEW_COUNT
      , WRITE_AT
)
VALUES (
       #{boardConfigIdx}
     , #{boardCategoryConfigIdx}
     , #{userIdx}
     , #{status}
     , #{title}
     , #{contents}
     , 0
     , NOW()
       )

mapper.update:
UPDATE Board
   SET BOARD_CONFIG_IDX = #{boardConfigIdx}
     , BOARD_CATEGORY_CONFIG_IDX = #{boardCategoryConfigIdx}
     , STATUS = #{status}
     , TITLE = #{title}
     , CONTENTS = #{contents}
     , UPDATE_AT = NOW()
WHERE IDX = #{idx}

UPDATE Board
   SET VIEW_COUNT = VIEW_COUNT + 1
 WHERE IDX = #{idx}

mapper.delete:
UPDATE Board
   SET STATUS = 'D'
WHERE IDX = #{idx}
