mapper.select:
SELECT IDX , STATUS , NAME , TEL , CATEGORY , EMPLOYEE_COUNT , ADDRESS , CREATE_AT FROM Company
WHERE NAME LIKE CONCAT('%',#{pageDTO.keyword},'%') OR TEL LIKE CONCAT('%',#{pageDTO.keyword},'%') OR CEO LIKE CONCAT('%',#{pageDTO.keyword},'%') OR ADDRESS LIKE CONCAT('%',#{pageDTO.keyword},'%')
ORDER BY IDX DESC LIMIT #{pageDTO.pageIndex}, #{pageDTO.pageSize}

mapper.insert:
 INSERT INTO
        Company (
        USER_IDX
      , STATUS
      , NAME
      , TEL
      , ADDRESS
      , CATEGORY
      , CEO
      , EMPLOYEE_COUNT
      , CORPORATION_NUMBER
      , BUSINESS_NUMBER
      , CREATE_AT
      , WRITE_AT
 )
 VALUES (
        #{userIdx}
      , #{status}
      , #{name}
      , #{tel}
      , #{address}
      , #{category}
      , #{ceo}
      , #{employeeCount}
      , #{corporationNumber}
      , #{businessNumber}
      , #{createAt}
      , NOW()
)

mapper.update:
UPDATE Company
   SET STATU = #{status}
     , NAME = #{name}
     , TEL = #{tel}
     , ADDRESS = #{address}
     , CATEGORY = #{category}
     , EMPLOYEE_COUNT = #{employeeCount}
     , CORPORATION_NUMBER = #{corporationNumber}
     , BUSINESS_NUMBER = #{businessNumber}
     , UPDATE_AT = NOW()
 WHERE IDX = #{idx};
