mapper.select:
/* NOT_SQL_LOG */
SELECT COUNT(*) FROM Company WHERE CORPORATION_NUMBER = #{corporationNumber}

    AND IDX != #{idx}

/* NOT_SQL_LOG */
SELECT COUNT(*) FROM Company WHERE BUSINESS_NUMBER = #{businessNumber}

    AND IDX != #{idx}

/* NOT_SQL_LOG */ SELECT IDX , STATUS , NAME , TEL , CATEGORY , EMPLOYEE_COUNT , ADDRESS , CORPORATION_NUMBER , BUSINESS_NUMBER , MANAGER_NAME , MANAGER_EMAIL , MANAGER_PHONE , TOTAL_TIME , TOTAL_USED_TIME , AVAILABLE_TIME , SERVICE_START_AT , SERVICE_END_AT , CREATE_AT FROM Company
WHERE NAME LIKE CONCAT('%',#{pageDTO.keyword},'%') OR TEL LIKE CONCAT('%',#{pageDTO.keyword},'%') OR CEO LIKE CONCAT('%',#{pageDTO.keyword},'%') OR ADDRESS LIKE CONCAT('%',#{pageDTO.keyword},'%')
ORDER BY IDX DESC LIMIT #{pageDTO.pageIndex}, #{pageDTO.pageSize}

mapper.insert:
 INSERT INTO
        Company (
        USER_IDX
      , STATUS
      , NAME
      , TEL
      , EMAIL
      , ADDRESS
      , CATEGORY
      , CEO
      , EMPLOYEE_COUNT
      , CORPORATION_NUMBER
      , BUSINESS_NUMBER
      , MANAGER_NAME
      , MANAGER_EMAIL
      , MANAGER_PHONE
      , CREATE_AT
      , WRITE_AT
 )
 VALUES (
        #{userIdx}
      , #{status}
      , #{name}
      , #{tel}
      , #{email}
      , #{address}
      , #{category}
      , #{ceo}
      , #{employeeCount}
      , #{corporationNumber}
      , #{businessNumber}
      , #{managerName}
      , #{managerEmail}
      , #{managerPhone}
      , #{createAt}
      , NOW()
)

mapper.update:
/* NOT_SQL_LOG */
UPDATE Company
   SET STATUS = #{status}
     , NAME = #{name}
     , TEL = #{tel}
     , EMAIL = #{email}
     , ADDRESS = #{address}
     , CATEGORY = #{category}
     , EMPLOYEE_COUNT = #{employeeCount}
     , CORPORATION_NUMBER = #{corporationNumber}
     , BUSINESS_NUMBER = #{businessNumber}
     , TOTAL_TIME = #{totalTime}
     , TOTAL_USED_TIME = #{totalUsedTime}
     , AVAILABLE_TIME = #{availableTime}
     , SERVICE_START_AT = #{serviceStartAt}
     , SERVICE_END_AT = #{serviceEndAt}
     , UPDATE_AT = NOW()
 WHERE IDX = #{idx};
