mapper.select:
/* NOT_SQL_LOG */
SELECT IDX
     , FK_IDX
     , TABLES
  FROM Common_File WHERE FK_IDX = #{fkIdx}
   AND TABLES = #{tables}

mapper.insert:
INSERT INTO
       Common_File (
           TABLES
         , FK_IDX
         , TARGET_TYPE
)
VALUES (
         #{tables}
       , #{fkIdx}
       , #{targetType}
       )
