mapper.select:
/* NOT_SQL_LOG */ SELECT I.IDX , I.STATUS , I.MEMO , I.WRITE_AT , C.NAME AS COMPANY_NAME , U.NAME AS USER_NAME FROM Inquiry I LEFT JOIN Company AS C ON C.IDX = I.COMPANY_IDX LEFT JOIN User AS U ON U.IDX = I.USER_IDX WHERE 1 = 1
AND C.IDX = #{companyIdx}
AND C.NAME LIKE CONCAT('%', #{pageDTO.keyword}, '%') OR U.NAME LIKE CONCAT('%', #{pageDTO.keyword}, '%') OR I.MEMO LIKE CONCAT('%', #{pageDTO.keyword}, '%')
ORDER BY I.IDX DESC LIMIT #{pageDTO.pageIndex}, #{pageDTO.pageSize}

mapper.update:
/* NOT_SQL_LOG */
UPDATE Inquiry

           SET STATUS = "Y"


           SET STATUS = "N"

 WHERE IDX = #{idx}

mapper.delete:
/* NOT_SQL_LOG */
DELETE FROM Inquiry
 WHERE IDX = #{idx}

           AND COMPANY_IDX = #{companyIdx}

mapper.insert:
INSERT INTO
       Inquiry (
       STATUS
     , MEMO
     , USER_IDX
     , COMPANY_IDX
     , WRITE_IP
     , WRITE_AT
)
VALUES (
        "N"
      , #{memo}
      , #{userIdx}
      , #{companyIdx}
      , #{writeIp}
      , NOW()
        )

mapper.update:
/* NOT_SQL_LOG */
UPDATE Inquiry
   SET STATUS = "N"
     , MEMO = #{memo}
 WHERE IDX = #{idx}
   AND COMPANY_IDX = #{companyIdx}
