mapper.select:
/* NOT_SQL_LOG */
SELECT COUNT(*) FROM Mentor_Career
 WHERE IDX = #{idx}

mapper.insert:
INSERT INTO Mentor_Career
          ( MENTOR_IDX
          , STATUS
          , COMPANY
          , DEPARTMENT
          , GRADE
          , ANNUAL
          , START_AT
          , END_AT
          , WRITE_AT
          )
   VALUES ( #{mentorIdx}
          , #{status}
          , #{company}
          , #{department}
          , #{grade}
          , #{annual}
          , #{startAt}
          , #{endAt}
          , NOW()
          )

mapper.update:
/* NOT_SQL_LOG */
UPDATE Mentor_Career
   SET STATUS = #{status}
     , COMPANY = #{company}
     , DEPARTMENT = #{department}
     , GRADE = #{grade}
     , ANNUAL = #{annual}
     , START_AT = #{startAt}
     , END_AT = #{endAt}
WHERE IDX = #{idx}

mapper.delete:
/* NOT_SQL_LOG */
DELETE FROM Mentor_Career
 WHERE IDX = #{idx};
