mapper.select:
/* NOT_SQL_LOG */
SELECT COUNT(*) FROM Mentor_Certificate
 WHERE IDX = #{idx}

mapper.insert:
INSERT INTO Mentor_Certificate
          ( MENTOR_IDX
          , NAME
          , CONTENTS
          , ACCEPTANCE_ORGANIZATION
          , EXPIRATION_AT
          , WRITE_AT
          )
   VALUES ( #{mentorIdx}
          , #{name}
          , #{contents}
          , #{acceptanceOrganization}
          , #{expirationAt}
          , NOW()
          )

mapper.update:
/* NOT_SQL_LOG */
UPDATE Mentor_Certificate
   SET NAME = #{name}
     , CONTENTS = #{contents}
     , ACCEPTANCE_ORGANIZATION = #{acceptanceOrganization}
     , EXPIRATION_AT = #{expirationAt}
 WHERE IDX = #{idx}

mapper.delete:
/* NOT_SQL_LOG */
DELETE FROM Mentor_Certificate
 WHERE IDX = #{idx}
