mapper.select:
/* NOT_SQL_LOG */
SELECT COUNT(*) FROM User
 WHERE EMAIL = #{email}

/* NOT_SQL_LOG */
SELECT COUNT(*) FROM User
 WHERE PHONE = #{phone}

    AND IDX != #{idx}

mapper.update:
/* NOT_SQL_LOG */
UPDATE User
   SET VISIT_AT = NOW()
 WHERE EMAIL = #{email}

mapper.select:
/* NOT_SQL_LOG */ SELECT IDX , NAME , EMAIL , ROLE , ROLE_CODE_IDX , STATUS , WRITE_AT , VISIT_AT FROM User
WHERE NAME LIKE CONCAT('%',#{pageDTO.keyword},'%') OR EMAIL LIKE CONCAT('%',#{pageDTO.keyword},'%') OR PHONE LIKE CONCAT('%',#{pageDTO.keyword},'%')
ORDER BY IDX DESC LIMIT #{pageDTO.pageIndex}, #{pageDTO.pageSize}

mapper.insert:
/* NOT_SQL_LOG */
INSERT INTO User
          ( EMAIL
          , PASSWORD
          , ROLE
          , ROLE_CODE_IDX
          , STATUS
          , NAME
          , PHONE
          , GENDER
          , IS_AGREEMENT
          , WRITE_AT
          )
   VALUES ( #{email}
          , #{password}
          , #{role}
          , #{roleCodeIdx}
          , #{status}
          , #{name}
          , #{phone}
          , #{gender}
          , #{isAgreement}
          , NOW()
          )

mapper.update:
/* NOT_SQL_LOG */
UPDATE User
   SET ROLE = #{role}
     , ROLE_CODE_IDX = #{roleCodeIdx}
     , STATUS = #{status}

        , PASSWORD = #{password}

    , PHONE = #{phone}
    , GENDER = #{gender}
    , IS_AGREEMENT = #{isAgreement}
WHERE IDX = #{idx}

mapper.delete:
/* NOT_SQL_LOG */
DELETE FROM User
 WHERE IDX = #{idx}
